<?php
require 'vendor/autoload.php';
$app = require_once 'bootstrap/app.php';
$app->make(\Illuminate\Contracts\Console\Kernel::class)->bootstrap();

use App\Models\Department;

// Imagen JPEG base64 real más grande (500x300)
$imageBase64 = '';

$departments = Department::all();

foreach ($departments as $index => $dept) {
    try {
        $dept->update(['images_binary' => $imageBase64]);
        echo "✓ {$dept->name}: imagen actualizada" . PHP_EOL;
    } catch (\Exception $e) {
        echo "✗ {$dept->name}: {$e->getMessage()}" . PHP_EOL;
    }
}

echo PHP_EOL . "✓ ¡Imágenes grandes creadas! Ahora recarga la app." . PHP_EOL;
